package org.p3g.canvas.examples;import scala.scalajs.js.JSApp;import org.scalajs.dom;import dom.document;import org.scalajs.dom.html.Canvas;object Main extends JSApp {  def main:Unit = {    val canvas = dom.document.createElement("canvas").asInstanceOf[Canvas];	val ctx = canvas.getContext("2d").asInstanceOf[dom.CanvasRenderingContext2D];	canvas.width = (0.95 * dom.window.innerWidth).toInt;    canvas.height = (0.95 * dom.window.innerHeight).toInt;	dom.document.body.appendChild(canvas);	ctx.beginPath();	ctx.arc(100,75,50,0,2*Math.PI);	ctx.stroke();  }}